mapper.select:
SELECT d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name AS category_name
FROM dish d left outer join category c on d.category_id = c.id

    and d.name like concat('%',#{name},'%')
     and category_id = #{categoryId}
     and d.status = #{status}

order by d.update_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}
